1 = 1

	AND cname LIKE #{cname}

	ESCAPE '\'


	AND ur.croleid IN (${roleIds})


	and ccreatedatetime >= #{ccreatedatetimeStart}


	and ccreatedatetime <= #{ccreatedatetimeEnd}


	and cmodifydatetime >= #{cmodifydatetimeStart}


	and cmodifydatetime <= #{cmodifydatetimeEnd}

mapper.select:
		SELECT
		DISTINCT(t.cid), t.ccreatedatetime,t.cmodifydatetime, t.cname, t.cpwd
		FROM tuser t 
  		LEFT JOIN tusertrole ur ON t.cid = ur.cuserid



		${orderByClause}
		limit #{offset,jdbcType=DECIMAL} , #{limit,jdbcType=DECIMAL}
